method.body:
try {
  if (!prefManager.getPref("chat.sounds"))
    return;
  if (this._player) {
    var url = this._ios.newURI("chrome://oneteam/content/data/sounds/"+
                               type+".wav", null, null);
    this._player.play(url);
  } else if (this._flash)
    this._flash.playSound(document.location.toString().
      replace(/(?:jar:)?(.*?)(?:(?:\/[^\/!]+!\/)|\/content\/).*/, "$1/sounds/"+type+".mp3"),
      loops);
} catch(ex){ alert(ex)}
